import React from 'react';
import { useNavigate } from 'react-router-dom';
import Notifications from './Notifications';
import { AppBar, Toolbar, Typography, Button, Box, Avatar } from '@mui/material';

function Navbar({ user, onLogout }) {
  const navigate = useNavigate();

  const handleLogout = () => {
    if (onLogout) onLogout();
    navigate('/');
  };

  return (
    <AppBar position="static" color="primary" elevation={2}>
      <Toolbar sx={{ justifyContent: 'space-between' }}>
        <Box sx={{ display: 'flex', alignItems: 'center', cursor: 'pointer' }} onClick={() => navigate('/dashboard')}>
          <Avatar sx={{ bgcolor: '#fff', color: '#1976d2', mr: 1, width: 36, height: 36 }}>
            RH
          </Avatar>
          <Typography variant="h6" fontWeight={700} color="inherit">
            Research Hive
          </Typography>
        </Box>
        {user && (
          <Box sx={{ display: 'flex', alignItems: 'center', gap: 2 }}>
            <Avatar sx={{ bgcolor: '#fff', color: '#1976d2', width: 32, height: 32 }}>
              {user.name ? user.name[0].toUpperCase() : ''}
            </Avatar>
            <Typography variant="body1" color="inherit" sx={{ fontWeight: 500 }}>
              {user.name}
            </Typography>
            <Notifications user={user} />
            <Button color="inherit" onClick={() => navigate('/dashboard')}>
              Home
            </Button>
            <Button color="inherit" onClick={() => navigate('/profile')}>
              Profile
            </Button>
            <Button color="inherit" onClick={handleLogout}>
              Logout
            </Button>
          </Box>
        )}
      </Toolbar>
    </AppBar>
  );
}

export default Navbar;
