import React, { useEffect, useState } from 'react';

function Connections({ user }) {
  const [search, setSearch] = useState('');
  const [users, setUsers] = useState([]);
  const [connections, setConnections] = useState([]);
  const [requests, setRequests] = useState([]);
  const [sentRequests, setSentRequests] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [message, setMessage] = useState('');

  useEffect(() => {
    setLoading(true);
    Promise.all([
      fetch('/api/user/all').then(res => res.json()),
      fetch(`/api/connections/${user.id}`).then(res => res.json())
    ])
      .then(([usersData, connectionsData]) => {
        setUsers(usersData);
        setConnections(connectionsData.filter(c => c.status === 'accepted'));
        setRequests(connectionsData.filter(c => c.status === 'pending' && c.recipient._id === user.id));
        setSentRequests(connectionsData.filter(c => c.status === 'pending' && c.requester._id === user.id));
        setLoading(false);
      })
      .catch(() => {
        setError('Failed to load data.');
        setLoading(false);
      });
  }, [user]);

  const sendRequest = async (recipientId) => {
    setMessage('');
    setError('');
    try {
      const res = await fetch('/api/connections/request', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ requesterId: user.id, recipientId })
      });
      if (!res.ok) {
        const data = await res.json();
        setError(data.message || 'Failed to send request.');
      } else {
        setMessage('Connection request sent.');
        window.location.reload();
      }
    } catch {
      setError('Failed to send request.');
    }
  };

  const acceptRequest = async (connectionId) => {
    setMessage('');
    setError('');
    try {
      await fetch('/api/connections/accept', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ connectionId })
      });
      setMessage('Connection accepted.');
      window.location.reload();
    } catch {
      setError('Failed to accept request.');
    }
  };

  const rejectRequest = async (connectionId) => {
    setMessage('');
    setError('');
    try {
      await fetch('/api/connections/reject', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ connectionId })
      });
      setMessage('Connection rejected.');
      window.location.reload();
    } catch {
      setError('Failed to reject request.');
    }
  };

  const isConnected = (userId) =>
    connections.some(c =>
      (c.requester._id === user.id && c.recipient._id === userId) ||
      (c.recipient._id === user.id && c.requester._id === userId)
    );

  const isRequestSent = (userId) =>
    sentRequests.some(r => r.recipient._id === userId);

  return (
    <div style={{ maxWidth: 600, margin: '0 auto', padding: 20 }}>
      <h2>Connections</h2>
      {loading && <p>Loading...</p>}
      {error && <div style={{ color: 'red' }}>{error}</div>}
      {message && <div style={{ color: 'green' }}>{message}</div>}
      <div style={{ marginBottom: 20 }}>
        <input
          type="text"
          placeholder="Search users by name"
          value={search}
          onChange={e => setSearch(e.target.value)}
          style={{ width: '100%', padding: 8, marginBottom: 10 }}
        />
        <div>
          {users
            .filter(u => u.name.toLowerCase().includes(search.toLowerCase()) && u._id !== user.id)
            .map(u => (
              <div key={u._id} style={{
                border: '1px solid #ccc',
                borderRadius: 8,
                padding: 10,
                marginBottom: 10,
                display: 'flex',
                justifyContent: 'space-between',
                alignItems: 'center'
              }}>
                <span>
                  <strong>{u.name}</strong> <span style={{ color: '#888' }}>({u.email})</span>
                </span>
                {isConnected(u._id) ? (
                  <span style={{ color: 'green', fontWeight: 'bold' }}>Connected</span>
                ) : isRequestSent(u._id) ? (
                  <span style={{ color: 'orange', fontWeight: 'bold' }}>Request Sent</span>
                ) : (
                  <button onClick={() => sendRequest(u._id)}>Send Request</button>
                )}
              </div>
            ))}
        </div>
      </div>
      <h3>Pending Requests</h3>
      <div>
        {requests.length === 0 && <p>No pending requests.</p>}
        {requests.map(r => (
          <div key={r._id} style={{
            border: '1px solid #eee',
            borderRadius: 8,
            padding: 10,
            marginBottom: 10,
            display: 'flex',
            justifyContent: 'space-between',
            alignItems: 'center'
          }}>
            <span>
              <strong>{r.requester.name}</strong> wants to connect.
            </span>
            <span>
              <button onClick={() => acceptRequest(r._id)} style={{ marginRight: 8 }}>Accept</button>
              <button onClick={() => rejectRequest(r._id)}>Reject</button>
            </span>
          </div>
        ))}
      </div>
      <h3>Your Connections</h3>
      <div>
        {connections.length === 0 && <p>No connections yet.</p>}
        {connections.map(c => {
          const otherUser = c.requester._id === user.id ? c.recipient : c.requester;
          return (
            <div key={c._id} style={{
              border: '1px solid #eee',
              borderRadius: 8,
              padding: 10,
              marginBottom: 10
            }}>
              <strong>{otherUser.name}</strong> <span style={{ color: '#888' }}>({otherUser.email})</span>
            </div>
          );
        })}
      </div>
    </div>
  );
}

export default Connections;
