import React from 'react';
import { Container, Box, Typography, Paper, Grid, Card, CardContent, CardActionArea } from '@mui/material';
import { Link as RouterLink, useNavigate } from 'react-router-dom';
import Button from '@mui/material/Button';

const features = [
  {
    title: 'Researcher Profile',
    description: 'View and edit your biography, interests, publications, and more.',
    color: 'primary',
    to: '/profile'
  },
  {
    title: 'Repository',
    description: 'Upload, manage, and search your research files.',
    color: 'success',
    to: '/repository'
  },
  {
    title: 'Forum',
    description: 'Discuss ideas, issues, and collaborate with other researchers.',
    color: 'info',
    to: '/forum'
  },
  {
    title: 'Connections',
    description: 'Find, connect, and collaborate with other researchers.',
    color: 'secondary',
    to: '/connections'
  }
];

function Home() {
  const navigate = useNavigate();

  return (
    <Container maxWidth="lg">
      <Box mt={8} mb={4}>
        <Typography variant="h3" fontWeight={700} align="center" gutterBottom sx={{ letterSpacing: 2 }}>
          Welcome to <span style={{ color: '#1976d2' }}>Research Hive</span>
        </Typography>
        <Typography variant="h6" align="center" color="text.secondary" sx={{ mb: 4 }}>
          Your collaborative platform for research, networking, and innovation.
        </Typography>
      </Box>
      <Grid container spacing={4} justifyContent="center">
        {features.map((feature, idx) => (
          <Grid item xs={12} sm={6} md={3} key={feature.title}>
            <Card elevation={4} sx={{ borderRadius: 3, transition: '0.2s', '&:hover': { boxShadow: 8 } }}>
              <CardActionArea component={RouterLink} to={feature.to}>
                <CardContent sx={{ textAlign: 'center', py: 4 }}>
                  <Typography variant="h5" fontWeight={700} color={feature.color} gutterBottom>
                    {feature.title}
                  </Typography>
                  <Typography variant="body2" color="text.secondary" sx={{ minHeight: 48 }}>
                    {feature.description}
                  </Typography>
                  <Button
                    variant="contained"
                    color={feature.color}
                    size="large"
                    sx={{ mt: 3, fontWeight: 600, px: 4 }}
                  >
                    Go
                  </Button>
                </CardContent>
              </CardActionArea>
            </Card>
          </Grid>
        ))}
      </Grid>
      <Box mt={6} textAlign="center">
        <Typography variant="body1" color="text.secondary">
          Need help? Visit our <Button variant="text" color="primary" onClick={() => navigate('/forum')}>Forum</Button> or connect with other researchers!
        </Typography>
      </Box>
    </Container>
  );
}

export default Home;
