import React from 'react';
import { Container, Box, Typography, Grid, Card, CardContent, CardActionArea, Button } from '@mui/material';
import { Link as RouterLink, useNavigate } from 'react-router-dom';

const features = [
  {
    title: 'Profile',
    description: 'Edit your biography, interests, and research details.',
    to: '/profile'
  },
  {
    title: 'Repository',
    description: 'Upload and manage your research files.',
    to: '/repository'
  },
  {
    title: 'Forum',
    description: 'Discuss ideas and collaborate with peers.',
    to: '/forum'
  },
  {
    title: 'Connections',
    description: 'Find and connect with other researchers.',
    to: '/connections'
  }
  // Add more features here as you expand
];

function Home() {
  const navigate = useNavigate();

  return (
    <Container maxWidth="md">
      <Box mt={10} mb={6} textAlign="center">
        <Typography variant="h3" fontWeight={700} sx={{ letterSpacing: 1, mb: 2 }}>
          Research Hive
        </Typography>
        <Typography variant="subtitle1" color="text.secondary" sx={{ mb: 1 }}>
          A minimal, modern platform for researchers.
        </Typography>
        <Typography variant="body2" color="text.secondary">
          Collaborate, share, and grow your research network.
        </Typography>
      </Box>
      <Grid container spacing={3} justifyContent="center">
        {features.map((feature) => (
          <Grid item xs={12} sm={6} md={3} key={feature.title}>
            <Card
              elevation={0}
              sx={{
                border: '1px solid #e0e0e0',
                borderRadius: 3,
                transition: 'box-shadow 0.2s',
                '&:hover': { boxShadow: 4, borderColor: '#1976d2' },
                bgcolor: '#fafbfc'
              }}
            >
              <CardActionArea component={RouterLink} to={feature.to}>
                <CardContent sx={{ textAlign: 'center', py: 4 }}>
                  <Typography variant="h6" fontWeight={600} sx={{ mb: 1 }}>
                    {feature.title}
                  </Typography>
                  <Typography variant="body2" color="text.secondary" sx={{ minHeight: 40 }}>
                    {feature.description}
                  </Typography>
                  <Button
                    variant="outlined"
                    size="small"
                    sx={{
                      mt: 2,
                      borderRadius: 2,
                      textTransform: 'none',
                      fontWeight: 500,
                      px: 3
                    }}
                  >
                    Explore
                  </Button>
                </CardContent>
              </CardActionArea>
            </Card>
          </Grid>
        ))}
      </Grid>
      <Box mt={8} textAlign="center">
        <Typography variant="caption" color="text.secondary">
          Need help? Visit the <Button variant="text" color="primary" onClick={() => navigate('/forum')}>Forum</Button> or connect with peers!
        </Typography>
      </Box>
    </Container>
  );
}

export default Home;
