import React, { useEffect, useState } from 'react';

function Connections({ user }) {
  const [search, setSearch] = useState('');
  const [users, setUsers] = useState([]);
  const [connections, setConnections] = useState([]);
  const [requests, setRequests] = useState([]);

  useEffect(() => {
    // Fetch all users
    fetch('/api/user/all')
      .then(res => res.json())
      .then(data => setUsers(data));
    // Fetch connections
    fetch(`/api/connections/${user.id}`)
      .then(res => res.json())
      .then(data => {
        setConnections(data.filter(c => c.status === 'accepted'));
        setRequests(data.filter(c => c.status === 'pending' && c.recipient._id === user.id));
      });
  }, [user]);

  const sendRequest = async (recipientId) => {
    await fetch('/api/connections/request', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ requesterId: user.id, recipientId })
    });
    window.location.reload();
  };

  const acceptRequest = async (connectionId) => {
    await fetch('/api/connections/accept', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ connectionId })
    });
    window.location.reload();
  };

  const rejectRequest = async (connectionId) => {
    await fetch('/api/connections/reject', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ connectionId })
    });
    window.location.reload();
  };

  return (
    <div>
      <h2>Connections</h2>
      <input
        type="text"
        placeholder="Search users by name"
        value={search}
        onChange={e => setSearch(e.target.value)}
      />
      <ul>
        {users.filter(u => u.name.toLowerCase().includes(search.toLowerCase()) && u._id !== user.id).map(u => (
          <li key={u._id}>
            {u.name} ({u.email})
            <button onClick={() => sendRequest(u._id)}>Send Request</button>
          </li>
        ))}
      </ul>
      <h3>Pending Requests</h3>
      <ul>
        {requests.map(r => (
          <li key={r._id}>
            {r.requester.name} wants to connect.
            <button onClick={() => acceptRequest(r._id)}>Accept</button>
            <button onClick={() => rejectRequest(r._id)}>Reject</button>
          </li>
        ))}
      </ul>
      <h3>Your Connections</h3>
      <ul>
        {connections.map(c => (
          <li key={c._id}>
            {c.requester._id === user.id ? c.recipient.name : c.requester.name}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default Connections;
