import React, { useState, useEffect } from 'react';
import {
  Container, Box, Typography, Paper, TextField, Button, Grid,
} from '@mui/material';
import { Link as RouterLink } from 'react-router-dom';

function ResearcherProfile({ user }) {
  const [profile, setProfile] = useState({
    bio: '', interests: '', publications: '', projects: '', affiliations: '', contact: ''
  });
  const [editMode, setEditMode] = useState(false);
  const [loading, setLoading] = useState(true);
  const [msg, setMsg] = useState('');

  useEffect(() => {
    if (!user) return;
    setLoading(true);
    fetch(`http://localhost:5000/api/user/profile/${user.id}`)
      .then(res => res.json())
      .then(data => {
        setProfile({
          bio: data.bio || '',
          interests: data.interests || '',
          publications: data.publications || '',
          projects: data.projects || '',
          affiliations: data.affiliations || '',
          contact: data.contact || ''
        });
        setLoading(false);
      })
      .catch(() => setLoading(false));
  }, [user]);

  const handleChange = e => {
    setProfile({ ...profile, [e.target.name]: e.target.value });
  };

  const handleSave = async (e) => {
    e.preventDefault();
    setMsg('');
    try {
      const res = await fetch(`http://localhost:5000/api/user/profile/${user.id}`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(profile)
      });
      if (!res.ok) throw new Error('Failed to update profile');
      setEditMode(false);
      setMsg('Profile updated!');
    } catch {
      setMsg('Error updating profile');
    }
  };

  if (loading) return <div>Loading...</div>;

  return (
    <Container maxWidth="md">
      <Box mt={8} component={Paper} elevation={6} p={6} borderRadius={4} sx={{ background: '#fff' }}>
        <Grid container justifyContent="space-between" alignItems="center">
          <Grid item>
            <Typography variant="h4" fontWeight={700} gutterBottom>
              Researcher Profile
            </Typography>
          </Grid>
          <Grid item>
            <Button
              variant="outlined"
              color="primary"
              component={RouterLink}
              to="/dashboard"
              sx={{ fontWeight: 600 }}
            >
              Home
            </Button>
          </Grid>
        </Grid>
        {msg && <Typography color="primary" sx={{ mt: 2 }}>{msg}</Typography>}
        {editMode ? (
          <form onSubmit={handleSave}>
            <TextField
              label="Biography"
              name="bio"
              multiline
              rows={3}
              fullWidth
              margin="normal"
              value={profile.bio}
              onChange={handleChange}
            />
            <TextField
              label="Research Interests"
              name="interests"
              fullWidth
              margin="normal"
              value={profile.interests}
              onChange={handleChange}
            />
            <TextField
              label="Publications"
              name="publications"
              fullWidth
              margin="normal"
              value={profile.publications}
              onChange={handleChange}
            />
            <TextField
              label="Projects"
              name="projects"
              fullWidth
              margin="normal"
              value={profile.projects}
              onChange={handleChange}
            />
            <TextField
              label="Affiliations"
              name="affiliations"
              fullWidth
              margin="normal"
              value={profile.affiliations}
              onChange={handleChange}
            />
            <TextField
              label="Contact Info"
              name="contact"
              fullWidth
              margin="normal"
              value={profile.contact}
              onChange={handleChange}
            />
            <Button
              type="submit"
              variant="contained"
              color="primary"
              sx={{ mt: 3, fontWeight: 600 }}
            >
              Save Profile
            </Button>
          </form>
        ) : (
          <Box>
            <Typography variant="subtitle1" sx={{ mt: 2, fontWeight: 600 }}>Biography:</Typography>
            <Typography variant="body1">{profile.bio || <span style={{color:'#aaa'}}>No biography added.</span>}</Typography>
            <Typography variant="subtitle1" sx={{ mt: 2, fontWeight: 600 }}>Research Interests:</Typography>
            <Typography variant="body1">{profile.interests || <span style={{color:'#aaa'}}>No interests added.</span>}</Typography>
            <Typography variant="subtitle1" sx={{ mt: 2, fontWeight: 600 }}>Publications:</Typography>
            <Typography variant="body1">{profile.publications || <span style={{color:'#aaa'}}>No publications added.</span>}</Typography>
            <Typography variant="subtitle1" sx={{ mt: 2, fontWeight: 600 }}>Projects:</Typography>
            <Typography variant="body1">{profile.projects || <span style={{color:'#aaa'}}>No projects added.</span>}</Typography>
            <Typography variant="subtitle1" sx={{ mt: 2, fontWeight: 600 }}>Affiliations:</Typography>
            <Typography variant="body1">{profile.affiliations || <span style={{color:'#aaa'}}>No affiliations added.</span>}</Typography>
            <Typography variant="subtitle1" sx={{ mt: 2, fontWeight: 600 }}>Contact Info:</Typography>
            <Typography variant="body1">{profile.contact || <span style={{color:'#aaa'}}>No contact info added.</span>}</Typography>
            <Button
              variant="contained"
              color="primary"
              sx={{ mt: 4, fontWeight: 600 }}
              onClick={() => setEditMode(true)}
            >
              Edit Profile
            </Button>
          </Box>
        )}
      </Box>
    </Container>
  );
}

export default ResearcherProfile;
