import React from 'react';
import { useNavigate } from 'react-router-dom';
import Notifications from './Notifications';

function Navbar({ user, onLogout }) {
  const navigate = useNavigate();

  const handleLogout = () => {
    if (onLogout) onLogout();
    navigate('/');
  };

  return (
    <nav style={{
      display: 'flex',
      justifyContent: 'space-between',
      alignItems: 'center',
      padding: '1rem',
      background: '#1976d2',
      color: 'white'
    }}>
      <div style={{ fontWeight: 'bold', fontSize: '1.2rem', cursor: 'pointer' }} onClick={() => navigate('/dashboard')}>
        Research Hive
      </div>
      <div style={{ display: 'flex', alignItems: 'center' }}>
        {user && (
          <>
            <span style={{
              marginRight: '1.5rem',
              fontWeight: 500,
              fontSize: '1rem',
              background: '#fff',
              color: '#1976d2',
              borderRadius: '20px',
              padding: '0.3rem 1rem'
            }}>
              {user.name}
            </span>
            <Notifications user={user} />
            <button onClick={() => navigate('/dashboard')} style={{ marginRight: '1rem', marginLeft: '1rem' }}>
              Home
            </button>
            <button onClick={handleLogout}>
              Logout
            </button>
          </>
        )}
      </div>
    </nav>
  );
}

export default Navbar;
