import React, { useState } from 'react';
import {
  Container,
  Box,
  Typography,
  TextField,
  Button,
  Alert,
  Paper,
  Checkbox,
  FormControlLabel,
  Link,
} from '@mui/material';

function App() {
  const [username, setUsername] = useState('');
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [message, setMessage] = useState('');
  const [error, setError] = useState('');
  const [agree, setAgree] = useState(false);

  const handleRegister = async (e) => {
    e.preventDefault();
    setMessage('');
    setError('');

    if (!agree) {
      setError('You must agree to the Terms of service');
      return;
    }

    const response = await fetch('http://localhost:5000/api/auth/register', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ username, name, email, password }),
    });

    const data = await response.json();
    if (response.ok) {
      setMessage('Registration successful!');
      setUsername('');
      setName('');
      setEmail('');
      setPassword('');
      setAgree(false);
    } else {
      setError(data.message || 'Registration failed');
    }
  };

  return (
    <Container maxWidth="md">
      <Box
        mt={8}
        component={Paper}
        elevation={6}
        p={6}
        borderRadius={4}
        sx={{
          background: '#fff',
          maxWidth: 600,
          margin: 'auto',
        }}
      >
        <Typography
          variant="h3"
          fontWeight={700}
          align="center"
          gutterBottom
          sx={{ mb: 4 }}
        >
          {/* Colourful ResearchHive */}
          <span style={{ color: '#4285F4' }}>R</span>
          <span style={{ color: '#EA4335' }}>e</span>
          <span style={{ color: '#FBBC05' }}>s</span>
          <span style={{ color: '#34A853' }}>e</span>
          <span style={{ color: '#4285F4' }}>a</span>
          <span style={{ color: '#EA4335' }}>r</span>
          <span style={{ color: '#FBBC05' }}>c</span>
          <span style={{ color: '#34A853' }}>h</span>
          <span style={{ color: '#4285F4' }}>H</span>
          <span style={{ color: '#EA4335' }}>i</span>
          <span style={{ color: '#FBBC05' }}>v</span>
          <span style={{ color: '#34A853' }}>e</span>
          <span style={{ color: '#444', marginLeft: 8 }}>Registration</span>
        </Typography>
        <form onSubmit={handleRegister}>
          <TextField
            label="Username"
            variant="outlined"
            fullWidth
            margin="normal"
            value={username}
            onChange={e => setUsername(e.target.value)}
            required
            autoFocus
            sx={{ fontSize: '1.2rem' }}
          />
          <TextField
            label="Your Name"
            variant="outlined"
            fullWidth
            margin="normal"
            value={name}
            onChange={e => setName(e.target.value)}
            required
            sx={{ fontSize: '1.2rem' }}
          />
          <TextField
            label="Your Email"
            type="email"
            variant="outlined"
            fullWidth
            margin="normal"
            value={email}
            onChange={e => setEmail(e.target.value)}
            required
            sx={{ fontSize: '1.2rem' }}
          />
          <TextField
            label="Password"
            type="password"
            variant="outlined"
            fullWidth
            margin="normal"
            value={password}
            onChange={e => setPassword(e.target.value)}
            required
            sx={{ fontSize: '1.2rem' }}
          />
          <FormControlLabel
            control={
              <Checkbox
                checked={agree}
                onChange={e => setAgree(e.target.checked)}
                required
              />
            }
            label={
              <span>
                I agree all statements in{' '}
                <Link href="#" underline="hover">
                  Terms of service
                </Link>
              </span>
            }
            sx={{ mt: 1 }}
          />
          <Button
            type="submit"
            variant="contained"
            color="primary"
            fullWidth
            size="large"
            sx={{
              mt: 3,
              mb: 2,
              py: 2,
              fontWeight: 700,
              fontSize: '1.2rem',
              letterSpacing: 1,
            }}
          >
            Register
          </Button>
          <Box textAlign="center" mt={2}>
            <Link href="#" underline="hover" sx={{ fontSize: '1rem' }}>
              I am already member
            </Link>
          </Box>
          {message && <Alert severity="success" sx={{ mt: 2 }}>{message}</Alert>}
          {error && <Alert severity="error" sx={{ mt: 2 }}>{error}</Alert>}
        </form>
      </Box>
    </Container>
  );
}

export default App;
