import React, { useEffect, useState } from 'react';

function Connections({ user }) {
  const [search, setSearch] = useState('');
  const [users, setUsers] = useState([]);
  const [connections, setConnections] = useState([]);
  const [requests, setRequests] = useState([]);
  const [loading, setLoading] = useState(true);
  const [msg, setMsg] = useState('');

  useEffect(() => {
    async function fetchData() {
      setLoading(true);
      const usersRes = await fetch('/api/user/all');
      const usersData = await usersRes.json();
      setUsers(usersData);

      const connRes = await fetch(`/api/connections/${user.id}`);
      const connData = await connRes.json();
      setConnections(connData.filter(c => c.status === 'accepted'));
      setRequests(connData.filter(c => c.status === 'pending' && c.recipient._id === user.id));
      setLoading(false);
    }
    fetchData();
  }, [user]);

  const sendRequest = async (recipientId) => {
    setMsg('');
    const res = await fetch('/api/connections/request', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ requesterId: user.id, recipientId })
    });
    if (res.ok) {
      setMsg('Connection request sent!');
    } else {
      const err = await res.json();
      setMsg(err.message || 'Error sending request');
    }
  };

  const acceptRequest = async (connectionId) => {
    await fetch('/api/connections/accept', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ connectionId })
    });
    window.location.reload();
  };

  const rejectRequest = async (connectionId) => {
    await fetch('/api/connections/reject', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ connectionId })
    });
    window.location.reload();
  };

  return (
    <div style={{
      maxWidth: 800,
      margin: '40px auto',
      background: '#fff',
      borderRadius: 8,
      boxShadow: '0 2px 8px rgba(0,0,0,0.08)',
      padding: 32
    }}>
      <h2 style={{ marginBottom: 24, color: '#2d3748' }}>Connections</h2>
      <div style={{ marginBottom: 24 }}>
        <input
          type="text"
          placeholder="Search researchers by name"
          value={search}
          onChange={e => setSearch(e.target.value)}
          style={{
            padding: '8px 12px',
            borderRadius: 4,
            border: '1px solid #ccc',
            width: '100%',
            maxWidth: 300
          }}
        />
      </div>
      {msg && <div style={{ color: '#3182ce', marginBottom: 16 }}>{msg}</div>}
      <section style={{ marginBottom: 32 }}>
        <h3 style={{ color: '#4a5568', marginBottom: 12 }}>Discover Researchers</h3>
        <ul style={{ listStyle: 'none', padding: 0 }}>
          {users.filter(u => u.name.toLowerCase().includes(search.toLowerCase()) && u._id !== user.id).map(u => (
            <li key={u._id} style={{
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'space-between',
              padding: '10px 0',
              borderBottom: '1px solid #eee'
            }}>
              <span>
                <strong>{u.name}</strong> <span style={{ color: '#718096' }}>({u.email})</span>
              </span>
              <button
                onClick={() => sendRequest(u._id)}
                style={{
                  background: '#3182ce',
                  color: '#fff',
                  border: 'none',
                  borderRadius: 4,
                  padding: '6px 16px',
                  cursor: 'pointer'
                }}
              >
                Connect
              </button>
            </li>
          ))}
        </ul>
      </section>
      <section style={{ marginBottom: 32 }}>
        <h3 style={{ color: '#4a5568', marginBottom: 12 }}>Pending Requests</h3>
        <ul style={{ listStyle: 'none', padding: 0 }}>
          {requests.length === 0 && <li style={{ color: '#718096' }}>No pending requests.</li>}
          {requests.map(r => (
            <li key={r._id} style={{
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'space-between',
              padding: '10px 0',
              borderBottom: '1px solid #eee'
            }}>
              <span>
                <strong>{r.requester.name}</strong> wants to connect.
              </span>
              <span>
                <button
                  onClick={() => acceptRequest(r._id)}
                  style={{
                    background: '#38a169',
                    color: '#fff',
                    border: 'none',
                    borderRadius: 4,
                    padding: '6px 12px',
                    marginRight: 8,
                    cursor: 'pointer'
                  }}
                >
                  Accept
                </button>
                <button
                  onClick={() => rejectRequest(r._id)}
                  style={{
                    background: '#e53e3e',
                    color: '#fff',
                    border: 'none',
                    borderRadius: 4,
                    padding: '6px 12px',
                    cursor: 'pointer'
                  }}
                >
                  Reject
                </button>
              </span>
            </li>
          ))}
        </ul>
      </section>
      <section>
        <h3 style={{ color: '#4a5568', marginBottom: 12 }}>Your Connections</h3>
        <ul style={{ listStyle: 'none', padding: 0 }}>
          {connections.length === 0 && <li style={{ color: '#718096' }}>No connections yet.</li>}
          {connections.map(c => (
            <li key={c._id} style={{
              padding: '10px 0',
              borderBottom: '1px solid #eee'
            }}>
              <strong>
                {c.requester._id === user.id ? c.recipient.name : c.requester.name}
              </strong>
            </li>
          ))}
        </ul>
      </section>
      {loading && <div style={{ color: '#718096', marginTop: 24 }}>Loading...</div>}
    </div>
  );
}

export default Connections;
