import React, { useState, useRef } from 'react';
import {
  Container,
  Box,
  Typography,
  Paper,
  TextField,
  Button,
  List,
  ListItem,
  ListItemText,
  IconButton,
  Chip,
  Grid,
} from '@mui/material';
import DownloadIcon from '@mui/icons-material/Download';
import DeleteIcon from '@mui/icons-material/Delete';
import HomeIcon from '@mui/icons-material/Home';
import { Link as RouterLink } from 'react-router-dom';

function Repository() {
  const [title, setTitle] = useState('');
  const [desc, setDesc] = useState('');
  const [tag, setTag] = useState('');
  const [search, setSearch] = useState('');
  const [repos, setRepos] = useState([]);
  const [file, setFile] = useState(null);
  const fileInputRef = useRef();

// Fetch repositories from backend on mount
React.useEffect(() => {
  fetch('http://localhost:5000/api/repository', {
    headers: {
      Authorization: `Bearer ${localStorage.getItem('token')}` // assuming token is stored
    }
  })
    .then(res => res.json())
    .then(data => setRepos(data))
    .catch(() => setRepos([]));
});

// Add new repository (with file upload)
const handleAdd = async (e) => {
  e.preventDefault();
  if (!file) return alert('Please select a file to upload.');

  const formData = new FormData();
  formData.append('title', title);
  formData.append('description', desc);
  formData.append('tags', tag);
  formData.append('file', file);

  try {
    const response = await fetch('http://localhost:5000/api/repository', {
      method: 'POST',
      headers: {
        Authorization: `Bearer ${localStorage.getItem('token')}`
      },
      body: formData
    });
    const newRepo = await response.json();
    setRepos([...repos, newRepo]);
    setTitle('');
    setDesc('');
    setTag('');
    setFile(null);
    fileInputRef.current.value = null;
  } catch (error) {
    console.error('Error uploading file:', error);
  }
};

// Delete repository
const handleDelete = async (repo) => {
  if (!window.confirm('Are you sure you want to delete this file?')) return;

  try {
    await fetch(`http://localhost:5000/api/repository/${repo._id}`, {
      method: 'DELETE',
      headers: {
        Authorization: `Bearer ${localStorage.getItem('token')}`
      }
    });
    setRepos(repos.filter(r => r._id !== repo._id));
  } catch (error) {
    console.error('Error deleting file:', error);
  }
};

// Download repository
const handleDownload = (repo) => {
  window.open(`http://localhost:5000/${repo.filePath}`, '_blank');
};

// Search filter
const filteredRepos = repos.filter(
  r =>
    r.title.toLowerCase().includes(search.toLowerCase()) ||
    r.description.toLowerCase().includes(search.toLowerCase()) ||
    r.tags.join(', ').toLowerCase().includes(search.toLowerCase())
);

  return (
    <Container maxWidth="md">
      <Box mt={8} component={Paper} elevation={6} p={6} borderRadius={4} sx={{ background: '#fff' }}>
        <Typography variant="h4" fontWeight={700} gutterBottom>
          Research Repository
        </Typography>
        <form onSubmit={handleAdd} style={{ marginBottom: 24 }}>
          <TextField
            label="Title"
            variant="outlined"
            fullWidth
            margin="normal"
            value={title}
            onChange={e => setTitle(e.target.value)}
            required
          />
          <TextField
            label="Description"
            variant="outlined"
            fullWidth
            margin="normal"
            value={desc}
            onChange={e => setDesc(e.target.value)}
            required
          />
          <TextField
            label="Tag"
            variant="outlined"
            fullWidth
            margin="normal"
            value={tag}
            onChange={e => setTag(e.target.value)}
            required
          />
          <Button
            variant="contained"
            component="label"
            color="primary"
            sx={{ mt: 2, fontWeight: 600 }}
          >
            Upload File
            <input
              type="file"
              hidden
              onChange={e => setFile(e.target.files[0])}
              ref={fileInputRef}
            />
          </Button>
          <Button
            type="submit"
            variant="contained"
            color="primary"
            sx={{ mt: 2, ml: 2, fontWeight: 600 }}
          >
            Submit Research
          </Button>
        </form>
        <TextField
          label="Search repositories"
          variant="outlined"
          fullWidth
          margin="normal"
          value={search}
          onChange={e => setSearch(e.target.value)}
        />
        <List>
          {filteredRepos.map(repo => (
            <ListItem
              key={repo.id}
              secondaryAction={
                <>
                  <IconButton edge="end" aria-label="download" onClick={() => handleDownload(repo)}>
                    <DownloadIcon />
                  </IconButton>
                  <IconButton edge="end" aria-label="delete" onClick={() => handleDelete(repo)}>
                    <DeleteIcon />
                  </IconButton>
                </>
              }
            >
              <ListItemText
                primary={repo.title}
                secondary={
                  <>
                    {repo.desc}
                    <Box mt={1}>
                      <Chip label={repo.tag} color="primary" size="small" />
                    </Box>
                  </>
                }
              />
            </ListItem>
          ))}
        </List>
      </Box>
    </Container>
  );
}

export default Repository;
