import React, { useState, useEffect } from 'react';
import {
  Container, Box, Typography, Paper, TextField, Button, Chip, Divider, Alert, Grid, Stack, Avatar
} from '@mui/material';
import ForumIcon from '@mui/icons-material/Forum';
import LightbulbIcon from '@mui/icons-material/Lightbulb';
import ReportProblemIcon from '@mui/icons-material/ReportProblem';

function Forum({ user }) {
  const [posts, setPosts] = useState([]);
  const [type, setType] = useState('idea');
  const [title, setTitle] = useState('');
  const [content, setContent] = useState('');
  const [message, setMessage] = useState('');
  const [error, setError] = useState('');
  const [commentText, setCommentText] = useState({});
  const [loading, setLoading] = useState(true);
  const [editId, setEditId] = useState(null);
  const [editTitle, setEditTitle] = useState('');
  const [editContent, setEditContent] = useState('');

  useEffect(() => {
    setLoading(true);
    fetch('http://localhost:5000/api/forum')
      .then(res => res.json())
      .then(data => {
        setPosts(data);
        setLoading(false);
      });
  }, []);

  // --- Handler functions (same as before) ---
  const handlePost = async (e) => {
    e.preventDefault();
    setMessage('');
    setError('');
    try {
      const res = await fetch('http://localhost:5000/api/forum', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          type,
          title,
          content,
          author: user.name,
          authorId: user.id
        })
      });
      const data = await res.json();
      if (!res.ok) {
        setError(data.error || 'Error posting');
        return;
      }
      setPosts([data, ...posts]);
      setTitle('');
      setContent('');
      setMessage('Posted!');
    } catch (err) {
      setError('Network error');
    }
  };

  const handleEdit = (post) => {
    setEditId(post._id);
    setEditTitle(post.title);
    setEditContent(post.content);
  };

  const handleEditSave = async (id) => {
    try {
      const res = await fetch(`http://localhost:5000/api/forum/${id}`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          userId: user.id,
          title: editTitle,
          content: editContent
        })
      });
      const data = await res.json();
      if (!res.ok) {
        setError(data.error || 'Error editing post');
        return;
      }
      setPosts(posts.map(p => p._id === id ? data : p));
      setEditId(null);
      setMessage('Post updated!');
    } catch (err) {
      setError('Network error');
    }
  };

  const handleDelete = async (id) => {
    if (!window.confirm('Are you sure you want to delete this post?')) return;
    try {
      const res = await fetch(`http://localhost:5000/api/forum/${id}`, {
        method: 'DELETE',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ userId: user.id })
      });
      const data = await res.json();
      if (!res.ok) {
        setError(data.error || 'Error deleting post');
        return;
      }
      setPosts(posts.filter(p => p._id !== id));
      setMessage('Post deleted!');
    } catch (err) {
      setError('Network error');
    }
  };

  const handleLike = async (id) => {
    const res = await fetch(`http://localhost:5000/api/forum/${id}/like`, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ userId: user.id, userName: user.name })
    });
    const data = await res.json();
    setPosts(posts.map(p => p._id === id ? data : p));
  };

  const handleComment = async (id) => {
    const text = commentText[id];
    if (!text) return;
    const res = await fetch(`http://localhost:5000/api/forum/${id}/comment`, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ userId: user.id, userName: user.name, text })
    });
    const data = await res.json();
    setPosts(posts.map(p => p._id === id ? data : p));
    setCommentText({ ...commentText, [id]: '' });
  };

  // --- UI ---
  return (
    <Container maxWidth="xl" sx={{ mt: 4 }}>
      <Grid container spacing={7} alignItems="flex-start">
        {/* Left: Post Form */}
        <Grid item xs={12} md={4} lg={3}>
          <Paper elevation={5} sx={{ p: 4, borderRadius: 4, minHeight: 420, bgcolor: '#f7faff' }}>
            <Stack direction="row" alignItems="center" spacing={1} mb={2}>
              <ForumIcon color="primary" fontSize="large" />
              <Typography variant="h5" fontWeight={700}>
                Create a Post
              </Typography>
            </Stack>
            {error && <Alert severity="error" sx={{ mb: 2 }}>{error}</Alert>}
            {message && <Alert severity="success" sx={{ mb: 2 }}>{message}</Alert>}
            <form onSubmit={handlePost}>
              <TextField
                select
                SelectProps={{ native: true }}
                label="Type"
                value={type}
                onChange={e => setType(e.target.value)}
                fullWidth
                sx={{ mb: 2 }}
              >
                <option value="idea">Idea</option>
                <option value="issue">Issue</option>
              </TextField>
              <TextField
                label="Title"
                value={title}
                onChange={e => setTitle(e.target.value)}
                required
                fullWidth
                sx={{ mb: 2 }}
              />
              <TextField
                label="Description"
                value={content}
                onChange={e => setContent(e.target.value)}
                required
                multiline
                rows={4}
                fullWidth
                sx={{ mb: 2 }}
              />
              <Button type="submit" variant="contained" color="primary" fullWidth size="large" sx={{ fontWeight: 600 }}>
                Post
              </Button>
            </form>
          </Paper>
        </Grid>
        {/* Right: Forum Posts */}
        <Grid item xs={2} md={8} lg={9}>
          <Paper elevation={5} sx={{ p: 4, borderRadius: 4, minHeight: 600, bgcolor: '#f9f9f9' }}>
            <Typography variant="h5" fontWeight={700} gutterBottom>
              Forum Posts
            </Typography>
            <Divider sx={{ mb: 3 }} />
            {loading ? (
              <Typography>Loading...</Typography>
            ) : posts.length === 0 ? (
              <Typography color="text.secondary">No posts yet.</Typography>
            ) : (
              <Stack spacing={3}>
                {posts.map(post => (
                  <Paper key={post._id} sx={{ p: 3, borderRadius: 3, bgcolor: '#fff' }} elevation={2}>
                    <Box display="flex" alignItems="center" justifyContent="space-between">
                      <Stack direction="row" alignItems="center" spacing={1}>
                        {post.type === 'idea' ? (
                          <LightbulbIcon color="primary" />
                        ) : (
                          <ReportProblemIcon color="error" />
                        )}
                        <Chip
                          label={post.type.charAt(0).toUpperCase() + post.type.slice(1)}
                          color={post.type === 'idea' ? 'primary' : 'secondary'}
                          size="small"
                        />
                        <Typography variant="h6" sx={{ ml: 1 }}>{post.title}</Typography>
                        <Typography variant="body2" color="textSecondary" sx={{ ml: 2 }}>
                          by <b>{post.author}</b>
                        </Typography>
                      </Stack>
                      {post.authorId === user.id && (
                        <Stack direction="row" spacing={1}>
                          <Button size="small" variant="outlined" onClick={() => handleEdit(post)}>Edit</Button>
                          <Button size="small" variant="outlined" color="error" onClick={() => handleDelete(post._id)}>Delete</Button>
                        </Stack>
                      )}
                    </Box>
                    {editId === post._id ? (
                      <Box mt={2}>
                        <TextField
                          label="Title"
                          value={editTitle}
                          onChange={e => setEditTitle(e.target.value)}
                          sx={{ mr: 1 }}
                        />
                        <TextField
                          label="Content"
                          value={editContent}
                          onChange={e => setEditContent(e.target.value)}
                          sx={{ mr: 1 }}
                        />
                        <Button variant="contained" size="small" onClick={() => handleEditSave(post._id)} sx={{ mr: 1 }}>
                          Save
                        </Button>
                        <Button variant="outlined" size="small" onClick={() => setEditId(null)}>
                          Cancel
                        </Button>
                      </Box>
                    ) : (
                      <Typography sx={{ mt: 2, mb: 1 }}>{post.content}</Typography>
                    )}
                    <Stack direction="row" alignItems="center" spacing={2} mt={2}>
                      <Button size="small" onClick={() => handleLike(post._id)}>
                        👍 {post.likes.length}
                      </Button>
                      <TextField
                        size="small"
                        placeholder="Add comment"
                        value={commentText[post._id] || ''}
                        onChange={e => setCommentText({ ...commentText, [post._id]: e.target.value })}
                        sx={{ width: 200 }}
                      />
                      <Button size="small" onClick={() => handleComment(post._id)}>Comment</Button>
                    </Stack>
                    <Box mt={2}>
                      {post.comments.map((c, i) => (
                        <Paper key={i} sx={{ p: 1.5, mb: 1, bgcolor: '#f5f5f5', display: 'flex', alignItems: 'center' }}>
                          <Avatar sx={{ width: 28, height: 28, mr: 1, bgcolor: '#1976d2', fontSize: 14 }}>
                            {c.userName ? c.userName.charAt(0).toUpperCase() : 'U'}
                          </Avatar>
                          <Box>
                            <Typography variant="caption" fontWeight={600}>{c.userName || c.user}</Typography>
                            <Typography variant="body2">{c.text}</Typography>
                            <Typography variant="caption" color="text.secondary">
                              {c.date ? new Date(c.date).toLocaleString() : ''}
                            </Typography>
                          </Box>
                        </Paper>
                      ))}
                    </Box>
                  </Paper>
                ))}
              </Stack>
            )}
          </Paper>
        </Grid>
      </Grid>
    </Container>
  );
}

export default Forum;
