import React, { useState, useEffect, useRef } from 'react';
import {
  Box, Typography, TextField, Button, Paper, Alert, CircularProgress, Stack, Avatar, IconButton
} from '@mui/material';
import EditIcon from '@mui/icons-material/Edit';
import PhotoCamera from '@mui/icons-material/PhotoCamera';

function ResearcherProfile({ user }) {
  const [profile, setProfile] = useState({
    name: '', // <-- add name
    bio: '', interests: '', publications: '', projects: '', affiliations: '', contact: '', photo: ''
  });
  const [editMode, setEditMode] = useState(false);
  const [loading, setLoading] = useState(true);
  const [msg, setMsg] = useState('');
  const [error, setError] = useState('');
  const [photoPreview, setPhotoPreview] = useState('');
  const fileInputRef = useRef();

  useEffect(() => {
    if (!user) return;
    setLoading(true);
    fetch(`http://localhost:5000/api/user/profile/${user.id}`)
      .then(res => res.json())
      .then(data => {
        setProfile({
          name: data.name || '',
          bio: data.bio || '',
          interests: data.interests || '',
          publications: data.publications || '',
          projects: data.projects || '',
          affiliations: data.affiliations || '',
          contact: data.contact || '',
          photo: data.photo || ''
        });
        setPhotoPreview(data.photo || '');
        setLoading(false);
      })
      .catch(() => {
        setError('Failed to load profile');
        setLoading(false);
      });
  }, [user]);

  const handleChange = e => {
    setProfile({ ...profile, [e.target.name]: e.target.value });
  };

  // Handle photo selection and preview
  const handlePhotoChange = e => {
    const file = e.target.files[0];
    if (file) {
      setProfile({ ...profile, photo: file });
      setPhotoPreview(URL.createObjectURL(file));
    }
  };

  // Save profile (with photo upload if changed)
  const handleSave = async (e) => {
    e.preventDefault();
    setMsg('');
    setError('');
    try {
      let photoUrl = profile.photo;
      // If photo is a File, upload it first (requires backend support)
      if (profile.photo && profile.photo instanceof File) {
        const formData = new FormData();
        formData.append('photo', profile.photo);
        const uploadRes = await fetch(`http://localhost:5000/api/user/profile/${user.id}/photo`, {
          method: 'POST',
          body: formData
        });
        const uploadData = await uploadRes.json();
        if (uploadRes.ok && uploadData.photoUrl) {
          photoUrl = uploadData.photoUrl;
        } else {
          setError('Photo upload failed');
          return;
        }
      }
      // Save profile fields
      const res = await fetch(`http://localhost:5000/api/user/profile/${user.id}`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ ...profile, photo: photoUrl })
      });
      const data = await res.json();
      if (!res.ok) {
        setError(data.message || 'Error updating profile');
        return;
      }
      setProfile({
        name: data.name || '',
        bio: data.bio || '',
        interests: data.interests || '',
        publications: data.publications || '',
        projects: data.projects || '',
        affiliations: data.affiliations || '',
        contact: data.contact || '',
        photo: data.photo || ''
      });
      setPhotoPreview(data.photo || '');
      setMsg('Profile updated!');
      setEditMode(false);
    } catch {
      setError('Network error');
    }
  };

  if (loading) return <Box display="flex" justifyContent="center" mt={6}><CircularProgress /></Box>;

  return (
    <Box maxWidth="sm" mx="auto" mt={6}>
      <Paper elevation={5} sx={{ p: 4, borderRadius: 4 }}>
        <Stack alignItems="center" spacing={2} mb={3}>
          <Box position="relative">
            <Avatar
              src={photoPreview}
              alt={profile.name}
              sx={{ width: 100, height: 100, fontSize: 40, bgcolor: '#1976d2' }}
            >
              {profile.name ? profile.name.charAt(0).toUpperCase() : ''}
            </Avatar>
            {editMode && (
              <>
                <input
                  type="file"
                  accept="image/*"
                  style={{ display: 'none' }}
                  ref={fileInputRef}
                  onChange={handlePhotoChange}
                />
                <IconButton
                  color="primary"
                  sx={{
                    position: 'absolute',
                    bottom: 0,
                    right: 0,
                    bgcolor: '#fff',
                    boxShadow: 1
                  }}
                  onClick={() => fileInputRef.current.click()}
                >
                  <PhotoCamera />
                </IconButton>
              </>
            )}
          </Box>
          <Typography variant="h5" fontWeight={700}>
            {editMode ? (
              <TextField
                name="name"
                label="Name"
                value={profile.name}
                onChange={handleChange}
                variant="standard"
                sx={{ fontSize: 28, fontWeight: 700 }}
                inputProps={{ style: { fontSize: 28, fontWeight: 700, textAlign: 'center' } }}
              />
            ) : (
              profile.name
            )}
          </Typography>
        </Stack>
        {error && <Alert severity="error" sx={{ mb: 2 }}>{error}</Alert>}
        {msg && <Alert severity="success" sx={{ mb: 2 }}>{msg}</Alert>}
        {editMode ? (
          <form onSubmit={handleSave}>
            <Stack spacing={2}>
              <TextField label="Bio" name="bio" value={profile.bio} onChange={handleChange} multiline rows={2} />
              <TextField label="Interests" name="interests" value={profile.interests} onChange={handleChange} />
              <TextField label="Publications" name="publications" value={profile.publications} onChange={handleChange} />
              <TextField label="Projects" name="projects" value={profile.projects} onChange={handleChange} />
              <TextField label="Affiliations" name="affiliations" value={profile.affiliations} onChange={handleChange} />
              <TextField label="Contact" name="contact" value={profile.contact} onChange={handleChange} />
              <Stack direction="row" spacing={2}>
                <Button type="submit" variant="contained" color="primary">Save</Button>
                <Button variant="outlined" onClick={() => setEditMode(false)}>Cancel</Button>
              </Stack>
            </Stack>
          </form>
        ) : (
          <Box>
            <Typography sx={{ mb: 1 }}><b>Bio:</b> {profile.bio}</Typography>
            <Typography sx={{ mb: 1 }}><b>Interests:</b> {profile.interests}</Typography>
            <Typography sx={{ mb: 1 }}><b>Publications:</b> {profile.publications}</Typography>
            <Typography sx={{ mb: 1 }}><b>Projects:</b> {profile.projects}</Typography>
            <Typography sx={{ mb: 1 }}><b>Affiliations:</b> {profile.affiliations}</Typography>
            <Typography sx={{ mb: 1 }}><b>Contact:</b> {profile.contact}</Typography>
            <Button variant="contained" sx={{ mt: 2 }} onClick={() => setEditMode(true)} startIcon={<EditIcon />}>
              Edit Profile
            </Button>
          </Box>
        )}
      </Paper>
    </Box>
  );
}

export default ResearcherProfile;
