import React, { useEffect, useState } from 'react';

function getInitials(name) {
  return name.split(' ').map(n => n[0]).join('').toUpperCase();
}

function Connections({ user }) {
  const [search, setSearch] = useState('');
  const [users, setUsers] = useState([]);
  const [connections, setConnections] = useState([]);
  const [requests, setRequests] = useState([]);
  const [sentRequests, setSentRequests] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [message, setMessage] = useState('');

  useEffect(() => {
    setLoading(true);
    Promise.all([
      fetch('/api/user/all').then(res => res.json()),
      fetch(`/api/connections/${user.id}`).then(res => res.json())
    ])
      .then(([usersData, connectionsData]) => {
        setUsers(usersData);
        setConnections(connectionsData.filter(c => c.status === 'accepted'));
        setRequests(connectionsData.filter(c => c.status === 'pending' && c.recipient._id === user.id));
        setSentRequests(connectionsData.filter(c => c.status === 'pending' && c.requester._id === user.id));
        setLoading(false);
      })
      .catch(() => {
        setError('Failed to load data.');
        setLoading(false);
      });
  }, [user]);

  const sendRequest = async (recipientId) => {
    setMessage('');
    setError('');
    try {
      const res = await fetch('/api/connections/request', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ requesterId: user.id, recipientId })
      });
      if (!res.ok) {
        const data = await res.json();
        setError(data.message || 'Failed to send request.');
      } else {
        setMessage('Connection request sent.');
        window.location.reload();
      }
    } catch {
      setError('Failed to send request.');
    }
  };

  const acceptRequest = async (connectionId) => {
    setMessage('');
    setError('');
    try {
      await fetch('/api/connections/accept', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ connectionId })
      });
      setMessage('Connection accepted.');
      window.location.reload();
    } catch {
      setError('Failed to accept request.');
    }
  };

  const rejectRequest = async (connectionId) => {
    setMessage('');
    setError('');
    try {
      await fetch('/api/connections/reject', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ connectionId })
      });
      setMessage('Connection rejected.');
      window.location.reload();
    } catch {
      setError('Failed to reject request.');
    }
  };

  const isConnected = (userId) =>
    connections.some(c =>
      (c.requester._id === user.id && c.recipient._id === userId) ||
      (c.recipient._id === user.id && c.requester._id === userId)
    );

  const isRequestSent = (userId) =>
    sentRequests.some(r => r.recipient._id === userId);

  return (
    <div style={{
      maxWidth: 700,
      margin: '0 auto',
      padding: 24,
      fontFamily: 'Segoe UI, Arial, sans-serif'
    }}>
      <h2 style={{ marginBottom: 24 }}>Connections</h2>
      {loading && <p>Loading...</p>}
      {error && <div style={{ color: 'red', marginBottom: 10 }}>{error}</div>}
      {message && <div style={{ color: 'green', marginBottom: 10 }}>{message}</div>}

      <div style={{
        background: '#f9f9f9',
        padding: 16,
        borderRadius: 8,
        marginBottom: 24,
        boxShadow: '0 2px 8px rgba(0,0,0,0.03)'
      }}>
        <input
          type="text"
          placeholder="Search researchers by name"
          value={search}
          onChange={e => setSearch(e.target.value)}
          style={{
            width: '100%',
            padding: 10,
            borderRadius: 6,
            border: '1px solid #ccc',
            marginBottom: 10,
            fontSize: 16
          }}
        />
        <div>
          {users
            .filter(u => u.name.toLowerCase().includes(search.toLowerCase()) && u._id !== user.id)
            .map(u => (
              <div key={u._id} style={{
                display: 'flex',
                alignItems: 'center',
                background: '#fff',
                borderRadius: 8,
                boxShadow: '0 1px 4px rgba(0,0,0,0.04)',
                padding: 12,
                marginBottom: 10,
                justifyContent: 'space-between'
              }}>
                <div style={{ display: 'flex', alignItems: 'center' }}>
                  <div style={{
                    width: 40,
                    height: 40,
                    borderRadius: '50%',
                    background: '#0078d4',
                    color: '#fff',
                    display: 'flex',
                    alignItems: 'center',
                    justifyContent: 'center',
                    fontWeight: 'bold',
                    fontSize: 18,
                    marginRight: 12
                  }}>
                    {getInitials(u.name)}
                  </div>
                  <div>
                    <div style={{ fontWeight: 500 }}>{u.name}</div>
                    <div style={{ color: '#888', fontSize: 14 }}>{u.email}</div>
                  </div>
                </div>
                <div>
                  {isConnected(u._id) ? (
                    <span style={{
                      color: '#2e7d32',
                      fontWeight: 'bold',
                      background: '#e8f5e9',
                      padding: '4px 12px',
                      borderRadius: 6
                    }}>Connected</span>
                  ) : isRequestSent(u._id) ? (
                    <span style={{
                      color: '#ff9800',
                      fontWeight: 'bold',
                      background: '#fff3e0',
                      padding: '4px 12px',
                      borderRadius: 6
                    }}>Request Sent</span>
                  ) : (
                    <button
                      onClick={() => sendRequest(u._id)}
                      style={{
                        background: '#0078d4',
                        color: '#fff',
                        border: 'none',
                        borderRadius: 6,
                        padding: '6px 16px',
                        cursor: 'pointer',
                        fontWeight: 500
                      }}
                    >Send Request</button>
                  )}
                </div>
              </div>
            ))}
        </div>
      </div>

      <h3 style={{ marginBottom: 12 }}>Pending Requests</h3>
      <div>
        {requests.length === 0 && <p style={{ color: '#888' }}>No pending requests.</p>}
        {requests.map(r => (
          <div key={r._id} style={{
            display: 'flex',
            alignItems: 'center',
            background: '#fff',
            borderRadius: 8,
            boxShadow: '0 1px 4px rgba(0,0,0,0.04)',
            padding: 12,
            marginBottom: 10,
            justifyContent: 'space-between'
          }}>
            <div style={{ display: 'flex', alignItems: 'center' }}>
              <div style={{
                width: 36,
                height: 36,
                borderRadius: '50%',
                background: '#ff9800',
                color: '#fff',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                fontWeight: 'bold',
                fontSize: 16,
                marginRight: 10
              }}>
                {getInitials(r.requester.name)}
              </div>
              <span>
                <strong>{r.requester.name}</strong> wants to connect.
              </span>
            </div>
            <span>
              <button
                onClick={() => acceptRequest(r._id)}
                style={{
                  background: '#2e7d32',
                  color: '#fff',
                  border: 'none',
                  borderRadius: 6,
                  padding: '6px 12px',
                  marginRight: 8,
                  cursor: 'pointer',
                  fontWeight: 500
                }}>Accept</button>
              <button
                onClick={() => rejectRequest(r._id)}
                style={{
                  background: '#c62828',
                  color: '#fff',
                  border: 'none',
                  borderRadius: 6,
                  padding: '6px 12px',
                  cursor: 'pointer',
                  fontWeight: 500
                }}>Reject</button>
            </span>
          </div>
        ))}
      </div>

      <h3 style={{ marginBottom: 12 }}>Your Connections</h3>
      <div>
        {connections.length === 0 && <p style={{ color: '#888' }}>No connections yet.</p>}
        {connections.map(c => {
          const otherUser = c.requester._id === user.id ? c.recipient : c.requester;
          return (
            <div key={c._id} style={{
              display: 'flex',
              alignItems: 'center',
              background: '#fff',
              borderRadius: 8,
              boxShadow: '0 1px 4px rgba(0,0,0,0.04)',
              padding: 12,
              marginBottom: 10
            }}>
              <div style={{
                width: 36,
                height: 36,
                borderRadius: '50%',
                background: '#0078d4',
                color: '#fff',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                fontWeight: 'bold',
                fontSize: 16,
                marginRight: 10
              }}>
                {getInitials(otherUser.name)}
              </div>
              <div>
                <strong>{otherUser.name}</strong>
                <span style={{ color: '#888', fontSize: 14, marginLeft: 8 }}>({otherUser.email})</span>
              </div>
            </div>
          );
        })}
      </div>
    </div>
  );
}

export default Connections;
