import React from 'react';
import { Container, Box, Typography, Grid, Card, CardContent, CardActions, Button } from '@mui/material';
import { Link as RouterLink, useNavigate } from 'react-router-dom';

const features = [
  {
    title: 'Profile',
    description: 'Showcase your research journey, update your info, and connect your expertise.',
    color: '#1976d2',
    to: '/profile'
  },
  {
    title: 'Repository',
    description: 'Upload, organize, and share your research files and datasets.',
    color: '#43a047',
    to: '/repository'
  },
  {
    title: 'Forum',
    description: 'Engage in discussions, ask questions, and share ideas with peers.',
    color: '#0288d1',
    to: '/forum'
  },
  {
    title: 'Connections',
    description: 'Expand your network, collaborate, and manage your research contacts.',
    color: '#8e24aa',
    to: '/connections'
  }
];

function Home() {
  const navigate = useNavigate();

  return (
    <Container maxWidth="md">
      <Box
        sx={{
          mt: 8,
          mb: 4,
          p: 4,
          background: 'linear-gradient(90deg, #e3f2fd 0%, #fce4ec 100%)',
          borderRadius: 4,
          boxShadow: 3,
          textAlign: 'center'
        }}
      >
        <Typography variant="h3" fontWeight={700} sx={{ letterSpacing: 2, mb: 2 }}>
          Research Hive
        </Typography>
        <Typography variant="h6" color="text.secondary" sx={{ mb: 2 }}>
          Empowering researchers to connect, collaborate, and innovate.
        </Typography>
        <Button
          variant="contained"
          color="primary"
          size="large"
          sx={{ mt: 2, px: 5, borderRadius: 2 }}
          onClick={() => navigate('/forum')}
        >
          Explore Forum
        </Button>
      </Box>
      <Grid container spacing={4} sx={{ mt: 2 }}>
        {features.map((feature) => (
          <Grid item xs={12} sm={6} key={feature.title}>
            <Card
              sx={{
                borderRadius: 3,
                boxShadow: 2,
                transition: 'transform 0.2s',
                '&:hover': { transform: 'scale(1.03)', boxShadow: 6 }
              }}
            >
              <CardContent sx={{ background: feature.color, color: '#fff', borderTopLeftRadius: 12, borderTopRightRadius: 12 }}>
                <Typography variant="h5" fontWeight={700} sx={{ mb: 1 }}>
                  {feature.title}
                </Typography>
                <Typography variant="body2" sx={{ minHeight: 48 }}>
                  {feature.description}
                </Typography>
              </CardContent>
              <CardActions sx={{ justifyContent: 'center', py: 2 }}>
                <Button
                  variant="outlined"
                  sx={{
                    color: feature.color,
                    borderColor: feature.color,
                    fontWeight: 600,
                    px: 4,
                    borderRadius: 2,
                    '&:hover': { background: feature.color, color: '#fff' }
                  }}
                  component={RouterLink}
                  to={feature.to}
                >
                  Visit
                </Button>
              </CardActions>
            </Card>
          </Grid>
        ))}
      </Grid>
      <Box mt={6} textAlign="center">
        <Typography variant="body2" color="text.secondary">
          Looking for something? Use the search or visit your profile to get started!
        </Typography>
      </Box>
    </Container>
  );
}

export default Home;
