import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { Container, Box, Typography, Paper, CircularProgress } from '@mui/material';

function ForumPostDetail() {
  const { id } = useParams();
  const [post, setPost] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetch(`http://localhost:5000/api/forum/${id}`)
      .then(res => res.json())
      .then(data => {
        setPost(data);
        setLoading(false);
      })
      .catch(() => setLoading(false));
  }, [id]);

  if (loading) return <Box display="flex" justifyContent="center" mt={6}><CircularProgress /></Box>;
  if (!post) return <Box mt={6}><Typography color="error">Post not found.</Typography></Box>;

  return (
    <Container maxWidth="sm">
      <Paper elevation={4} sx={{ p: 4, borderRadius: 3, mt: 6 }}>
        <Typography variant="h4" fontWeight={700} gutterBottom>
          {post.title}
        </Typography>
        <Typography variant="subtitle1" color="text.secondary" gutterBottom>
          By {post.author}
        </Typography>
        <Typography variant="body1" sx={{ mt: 2 }}>
          {post.content}
        </Typography>
        {/* Add comments, likes, etc. here if needed */}
      </Paper>
    </Container>
  );
}

export default ForumPostDetail;
