import React, { useEffect, useState } from 'react';
import { Container, Box, Typography, Paper } from '@mui/material';
import { Link as RouterLink, useNavigate } from 'react-router-dom';
import Button from '@mui/material/Button';

function Home() {
  const navigate = useNavigate();
  const [connectedPosts, setConnectedPosts] = useState([]);
  const user = JSON.parse(localStorage.getItem('user'))?.name || '';

  useEffect(() => {
    if (user) {
      fetch(`http://localhost:5000/api/forum/connected/${user}`)
        .then(res => res.json())
        .then(setConnectedPosts);
    }
  }, [user]);

  const handleLogout = () => {
    // eita user session gula clear kore dibe
    navigate('/login');
  };

  return (
    <Container maxWidth="md">
      <Box
        mt={8}
        component={Paper}
        elevation={6}
        p={6}
        borderRadius={4}
        sx={{ background: '#fff' }}
      >
        <Typography variant="h3" fontWeight={700} align="center" gutterBottom>
          Welcome to ResearchHive!
          <Box textAlign="center" mt={4}>
            <Button
              variant="contained"
              color="primary"
              size="large"
              component={RouterLink}
              to="/profile"
              sx={{ fontWeight: 600, mr: 2 }}
            >
              Go to Researcher Profile
            </Button>
            <Button
              variant="contained"
              color="success"
              size="large"
              component={RouterLink}
              to="/repository"
              sx={{ fontWeight: 600, ml: 2 }}
            >
              Repository
            </Button>
            <Button
              variant="contained"
              color="info"
              size="large"
              component={RouterLink}
              to="/forum"
              sx={{ fontWeight: 600, ml: 2 }}
            >
              Forum
            </Button>
            <Button
              variant="outlined"
              color="secondary"
              size="large"
              sx={{ fontWeight: 600 }}
              onClick={handleLogout}
            >
              Logout
            </Button>
          </Box>
        </Typography>
        <Typography variant="h6" align="center" color="text.secondary">
          You have successfully logged in.
        </Typography>
        <Typography variant="h5" mt={4} mb={2}>
          Latest Posts from Your Connections
        </Typography>
        {connectedPosts.length === 0 ? (
          <Typography color="text.secondary">No recent posts from your connections.</Typography>
        ) : (
          connectedPosts.map(post => (
            <Paper key={post._id} sx={{ mb: 2, p: 2 }}>
              <Typography variant="subtitle1" fontWeight={600}>
                {post.title}
              </Typography>
              <Typography variant="body2">{post.content}</Typography>
              <Typography variant="caption" color="text.secondary">
                {post.author} â€¢ {new Date(post.createdAt).toLocaleString()}
              </Typography>
            </Paper>
          ))
        )}
      </Box>
    </Container>
  );
}

export default Home;
