import React, { useEffect, useState } from 'react';
import { Box, Typography, Paper, Button, Grid, Divider, CircularProgress } from '@mui/material';
import { Link as RouterLink } from 'react-router-dom';

function Bookmarks({ user }) {
  const [bookmarks, setBookmarks] = useState([]);
  const [loading, setLoading] = useState(true);
  const [forumDetails, setForumDetails] = useState({});
  const [repoDetails, setRepoDetails] = useState({});

  useEffect(() => {
    if (!user) return;
    setLoading(true);
    fetch(`http://localhost:5000/api/bookmarks/${user.id}`)
      .then(res => res.json())
      .then(async (data) => {
        setBookmarks(data);
        // Fetch details for each bookmark
        const forumIds = data.filter(b => b.type === 'forum').map(b => b.item);
        const repoIds = data.filter(b => b.type === 'repository').map(b => b.item);

        // Fetch forum post details
        const forumDetailsObj = {};
        for (const id of forumIds) {
          try {
            const res = await fetch(`http://localhost:5000/api/forum/${id}`);
            if (res.ok) {
              const post = await res.json();
              forumDetailsObj[id] = post;
            }
          } catch {}
        }
        setForumDetails(forumDetailsObj);

        // Fetch repository details (implement this API in backend if not present)
        const repoDetailsObj = {};
        for (const id of repoIds) {
          try {
            const res = await fetch(`http://localhost:5000/api/repository/${id}`);
            if (res.ok) {
              const repo = await res.json();
              repoDetailsObj[id] = repo;
            }
          } catch {}
        }
        setRepoDetails(repoDetailsObj);

        setLoading(false);
      });
  }, [user]);

  const handleRemove = async (bookmarkId) => {
    await fetch(`http://localhost:5000/api/bookmarks/${bookmarkId}`, { method: 'DELETE' });
    setBookmarks(bookmarks.filter(b => b._id !== bookmarkId));
  };

  if (loading) {
    return (
      <Box display="flex" justifyContent="center" mt={6}>
        <CircularProgress />
      </Box>
    );
  }

  return (
    <Box maxWidth="md" mx="auto" mt={6}>
      <Paper elevation={4} sx={{ p: 4, borderRadius: 3 }}>
        <Typography variant="h5" fontWeight={700} gutterBottom>
          Your Bookmarks
        </Typography>
        <Divider sx={{ mb: 3 }} />
        <Grid container spacing={2}>
          {bookmarks.length === 0 && (
            <Grid item xs={12}>
              <Typography color="text.secondary">No bookmarks yet.</Typography>
            </Grid>
          )}
          {bookmarks.map(b => (
            <Grid item xs={12} key={b._id}>
              <Paper sx={{ p: 2, display: 'flex', justifyContent: 'space-between', alignItems: 'center', gap: 2 }}>
                <Box>
                  {b.type === 'forum' && forumDetails[b.item] && (
                    <>
                      <Typography fontWeight={600}>
                        [Forum] {forumDetails[b.item].title}
                      </Typography>
                      <Typography variant="body2" color="text.secondary" sx={{ mb: 1 }}>
                        {forumDetails[b.item].content?.slice(0, 80)}...
                      </Typography>
                      <Button
                        variant="outlined"
                        color="info"
                        size="small"
                        component={RouterLink}
                        to={`/forum/${b.item}`}
                        target="_blank"
                        sx={{ mr: 1 }}
                      >
                        View Post
                      </Button>
                    </>
                  )}
                  {b.type === 'repository' && repoDetails[b.item] && (
                    <>
                      <Typography fontWeight={600}>
                        [Repository] {repoDetails[b.item].title}
                      </Typography>
                      <Typography variant="body2" color="text.secondary" sx={{ mb: 1 }}>
                        {repoDetails[b.item].description?.slice(0, 80)}...
                      </Typography>
                      <Button
                        variant="outlined"
                        color="success"
                        size="small"
                        component={RouterLink}
                        to={`/repository/${b.item}`}
                        target="_blank"
                        sx={{ mr: 1 }}
                      >
                        View Repository
                      </Button>
                    </>
                  )}
                  {/* If details not loaded */}
                  {((b.type === 'forum' && !forumDetails[b.item]) ||
                    (b.type === 'repository' && !repoDetails[b.item])) && (
                    <Typography color="text.secondary">Loading details...</Typography>
                  )}
                </Box>
                <Button color="error" variant="outlined" onClick={() => handleRemove(b._id)}>
                  Remove
                </Button>
              </Paper>
            </Grid>
          ))}
        </Grid>
      </Paper>
    </Box>
  );
}

export default Bookmarks;
