import React, { useState, useRef, useEffect } from 'react';
import {
  Container,
  Box,
  Typography,
  Paper,
  TextField,
  Button,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  IconButton,
  Chip,
  Grid,
  Alert,
} from '@mui/material';
import DownloadIcon from '@mui/icons-material/Download';
import DeleteIcon from '@mui/icons-material/Delete';
import HomeIcon from '@mui/icons-material/Home';
import { Link as RouterLink } from 'react-router-dom';

function Repository() {
  const [title, setTitle] = useState('');
  const [desc, setDesc] = useState('');
  const [tag, setTag] = useState('');
  const [search, setSearch] = useState('');
  const [repos, setRepos] = useState([]);
  const [file, setFile] = useState(null);
  const [success, setSuccess] = useState('');
  const [error, setError] = useState('');
  const fileInputRef = useRef();

  // Fetch repositories from backend on mount
  useEffect(() => {
    fetch('http://localhost:5000/api/repository', {
      headers: {
        Authorization: `Bearer ${localStorage.getItem('token')}`
      }
    })
      .then(res => res.json())
      .then(data => setRepos(data))
      .catch(() => setRepos([]));
  }, []);

  // Add new repository (with file upload)
  const handleAdd = async (e) => {
    e.preventDefault();
    setSuccess('');
    setError('');
    if (!file) return setError('Please select a file to upload.');

    const formData = new FormData();
    formData.append('title', title);
    formData.append('description', desc);
    formData.append('tags', tag);
    formData.append('file', file);

    try {
      const response = await fetch('http://localhost:5000/api/repository', {
        method: 'POST',
        headers: {
          Authorization: `Bearer ${localStorage.getItem('token')}`
        },
        body: formData
      });
      const newRepo = await response.json();
      setRepos([...repos, newRepo]);
      setTitle('');
      setDesc('');
      setTag('');
      setFile(null);
      fileInputRef.current.value = null;
      setSuccess('File uploaded successfully!');
    } catch (error) {
      setError('Error uploading file');
    }
  };

  // Delete repository
  const handleDelete = async (repo) => {
    if (!window.confirm('Are you sure you want to delete this file?')) return;
    setSuccess('');
    setError('');
    try {
      await fetch(`http://localhost:5000/api/repository/${repo._id}`, {
        method: 'DELETE',
        headers: {
          Authorization: `Bearer ${localStorage.getItem('token')}`
        }
      });
      setRepos(repos.filter(r => r._id !== repo._id));
      setSuccess('File deleted successfully!');
    } catch (error) {
      setError('Error deleting file');
    }
  };

  // Download repository
  const handleDownload = (repo) => {
    window.open(`http://localhost:5000/${repo.filePath}`, '_blank');
  };

  // Search filter
  const filteredRepos = repos.filter(
    r =>
      r.title.toLowerCase().includes(search.toLowerCase()) ||
      r.description.toLowerCase().includes(search.toLowerCase()) ||
      (Array.isArray(r.tags) ? r.tags.join(', ').toLowerCase() : r.tags.toLowerCase()).includes(search.toLowerCase())
  );

  return (
    <Container maxWidth="lg">
      <Box sx={{ background: '#f5f5f5', minHeight: '100vh', py: 4 }}>
        <Box sx={{ background: '#1976d2', color: '#fff', px: 3, py: 2, borderRadius: 2, mb: 4, display: 'flex', alignItems: 'center', justifyContent: 'space-between' }}>
          <Typography variant="h4" fontWeight={700}>
            Repository
          </Typography>
          <Button
            variant="contained"
            color="success"
            component={RouterLink}
            to="/home"
            sx={{ fontWeight: 600 }}
          >
            Dashboard
          </Button>
        </Box>
        <Box maxWidth={600} mx="auto" mb={4} component={Paper} elevation={3} p={3}>
          {success && <Alert severity="success" sx={{ mb: 2 }}>{success}</Alert>}
          {error && <Alert severity="error" sx={{ mb: 2 }}>{error}</Alert>}
          <TextField
            label="Search files..."
            variant="outlined"
            fullWidth
            margin="normal"
            value={search}
            onChange={e => setSearch(e.target.value)}
          />
          <Button
            variant="contained"
            color="primary"
            fullWidth
            sx={{ mb: 2, fontWeight: 600 }}
            onClick={() => {}} // Just for UI, search is live
          >
            Search
          </Button>
          <form onSubmit={handleAdd}>
            <TextField
              label="File Title"
              variant="outlined"
              fullWidth
              margin="normal"
              value={title}
              onChange={e => setTitle(e.target.value)}
              required
            />
            <TextField
              label="File Description"
              variant="outlined"
              fullWidth
              margin="normal"
              multiline
              rows={2}
              value={desc}
              onChange={e => setDesc(e.target.value)}
              required
            />
            <TextField
              label="Tags (comma-separated)"
              variant="outlined"
              fullWidth
              margin="normal"
              value={tag}
              onChange={e => setTag(e.target.value)}
              required
            />
            <Button
              variant="contained"
              component="label"
              color="primary"
              sx={{ mt: 2, fontWeight: 600 }}
              fullWidth
            >
              Choose file
              <input
                type="file"
                hidden
                onChange={e => setFile(e.target.files[0])}
                ref={fileInputRef}
              />
            </Button>
            <Button
              type="submit"
              variant="contained"
              color="primary"
              fullWidth
              sx={{ mt: 2, fontWeight: 600 }}
            >
              Upload File
            </Button>
          </form>
        </Box>
        <Box maxWidth={900} mx="auto" component={Paper} elevation={3} p={3}>
          <Typography variant="h6" fontWeight={700} sx={{ mb: 2 }}>
            Uploaded Files
          </Typography>
          <TableContainer>
            <Table>
              <TableHead>
                <TableRow sx={{ background: '#1976d2' }}>
                  <TableCell sx={{ color: '#fff', fontWeight: 700 }}>Title</TableCell>
                  <TableCell sx={{ color: '#fff', fontWeight: 700 }}>Description</TableCell>
                  <TableCell sx={{ color: '#fff', fontWeight: 700 }}>Tags</TableCell>
                  <TableCell sx={{ color: '#fff', fontWeight: 700 }}>Actions</TableCell>
                </TableRow>
              </TableHead>
              <TableBody>
                {filteredRepos.map(repo => (
                  <TableRow key={repo._id}>
                    <TableCell>
                      <RouterLink to="#" style={{ color: '#1976d2', textDecoration: 'underline' }}>
                        {repo.title}
                      </RouterLink>
                    </TableCell>
                    <TableCell>{repo.description}</TableCell>
                    <TableCell>
                      {Array.isArray(repo.tags)
                        ? repo.tags.map((t, i) => <Chip key={i} label={t} size="small" sx={{ mr: 0.5 }} />)
                        : <Chip label={repo.tags} size="small" />}
                    </TableCell>
                    <TableCell>
                      <Button
                        color="primary"
                        size="small"
                        sx={{ textTransform: 'none', fontWeight: 600 }}
                        onClick={() => handleDownload(repo)}
                      >
                        Download
                      </Button>
                      {' | '}
                      <Button
                        color="error"
                        size="small"
                        sx={{ textTransform: 'none', fontWeight: 600 }}
                        onClick={() => handleDelete(repo)}
                      >
                        Delete
                      </Button>
                    </TableCell>
                  </TableRow>
                ))}
                {filteredRepos.length === 0 && (
                  <TableRow>
                    <TableCell colSpan={4} align="center">
                      No files found.
                    </TableCell>
                  </TableRow>
                )}
              </TableBody>
            </Table>
          </TableContainer>
        </Box>
      </Box>
    </Container>
  );
}

export default Repository;
